mapper.select:
select * from paymentinfo where orderdate >= #{fromDate}
and
orderdate <= #{toDate}
and ${orderSelect} like '%'||#{orderResult}||'%'

mapper.update:

update prdtManagement
	set prdtQty = #{changeQty}
	 where
			 prdtCode = #{changePrdt}
	    and
	    	 prdtSize = #{changeSize}

mapper.select:

select * from 
	 returnInfo		
where 
	 returnSts = #{returnSts}


	 and
	 	returnDate = #{returnDate}

select distinct prdtcode,prdtsize, orderdate, prdtmininame , prdtbrand ,soldqty ,prdtqty ,prdtprice, (soldqty*prdtprice) as sum from prdtmanagement join paymentinfo using(prdtcode) where soldqty > 0
and orderdate >= #{fromDate}
and
orderdate <= #{toDate}
order by soldqty desc
